import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Authentication.css';
import { registerStudent, registerTeacher } from '../../../services/api.js';

const Role = ({ setIsLoggedIn }) => {
    const navigate = useNavigate();

    const handleRoleSelection = async (role) => {
        try {
            let username = `${role}_username`; // Replace with actual username input
            let password = `${role}_password`; // Replace with actual password input

            if (role === 'student') {
                await registerStudent(username, password); 
            } else {
                await registerTeacher(username, password); 
            }

            setIsLoggedIn(true); // Update login state
            navigate(role === 'student' ? '/student/home' : '/teacher/home'); // Navigate to respective dashboard
        } catch (error) {
            alert(error.message || 'Registration failed!');
        }
    };

    const handleLoginNavigation = () => {
        navigate('/auth/login/student'); // Navigate to student login page (default to student login)
    };

    return (
        <div className="role-selection">
            <h2>Select Your Role</h2>
            <button onClick={() => handleRoleSelection('student')}>Student Register</button>
            <button onClick={() => handleRoleSelection('teacher')}>Teacher Register</button>
            <p>Already registered? <button onClick={handleLoginNavigation}>Login</button></p>
        </div>
    );
};

export default Role;
