import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Header from './components/header/Header';
import StudentLoginComponent from './components/pages/authentication/StudentLogin';
import TeacherLoginComponent from './components/pages/authentication/TeacherLogin';
import TeacherRegisterComponent from './components/pages/authentication/TeacherRegisterComponent';
import StudentRegisterComponent from './components/pages/authentication/StudentRegisterComponent';
import Role from './components/pages/authentication/Role';
import StudentSidebar from './components/sidebar/StudentSidebar';
import TeacherSidebar from './components/sidebar/TeacherSidebar';
import HomePage from './components/pages/home/StudentHome';
import TeacherDashboard from './components/pages/home/TeacherHome';

import './App.css';

function App() {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [role, setRole] = useState('');

    useEffect(() => {
        const storedRole = localStorage.getItem('role');
        const token = localStorage.getItem('token');
        if (storedRole && token) {
            setRole(storedRole);
            setIsLoggedIn(true);
        }
    }, []);

    const handleLogin = (userRole) => {
        setRole(userRole);
        setIsLoggedIn(true);
        localStorage.setItem('role', userRole);
    };

    const handleLogout = () => {
        setRole('');
        setIsLoggedIn(false);
        localStorage.removeItem('role');
        localStorage.removeItem('token');
    };

    return (
        <Router>
            <div className="App">
                {isLoggedIn && <Header />}
                {isLoggedIn && role === 'student' && <StudentSidebar />}
                {isLoggedIn && role === 'teacher' && <TeacherSidebar />}
                <Routes>
                    {!isLoggedIn ? (
                        <>
                            <Route path="/auth/register/student" element={<StudentRegisterComponent setIsLoggedIn={handleLogin} />} />
                            <Route path="/auth/register/teacher" element={<TeacherRegisterComponent setIsLoggedIn={handleLogin} />} />
                            <Route path="/auth/login/student" element={<StudentLoginComponent setIsLoggedIn={handleLogin} />} />
                            <Route path="/auth/login/teacher" element={<TeacherLoginComponent setIsLoggedIn={handleLogin} />} />
                            <Route path="/auth/login" element={<Role />} />
                            <Route path="*" element={<Navigate to="/auth/login" />} />
                        </>
                    ) : (
                        <>
                            {role === 'student' && (
                                <>
                                    <Route path="/student/home" element={<HomePage />} />
                                    <Route path="*" element={<Navigate to="/student/home" />} />
                                </>
                            )}
                            {role === 'teacher' && (
                                <>
                                    <Route path="/teacher/home" element={<TeacherDashboard />} />
                                    <Route path="*" element={<Navigate to="/teacher/home" />} />
                                </>
                            )}
                        </>
                    )}
                </Routes>
            </div>
        </Router>
    );
}

export default App;
