import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Role from './components/pages/authentication/Role';
import StudentLogin from './components/pages/authentication/StudentLogin';
import TeacherLogin from './components/pages/authentication/TeacherLogin';

import StudentRegister from './components/pages/authentication/StudentRegisterComponent';
import TeacherRegister from './components/pages/authentication/TeacherRegisterComponent';

import StudentSidebar from './components/sidebar/StudentSidebar';
import TeacherSidebar from './components/sidebar/TeacherSidebar';

import SubscriptionsPage from './components/pages/subscription/Subscription';
import HomePage from './components/pages/home/StudentHome';
import History from './components/pages/history/History';
import LikedVideo from './components/pages/likedVideo/LikedVideo';
import StudentProfile from './components/pages/profile/StudentProfile';

import TeacherDashboard from './components/pages/home/TeacherHome';
import UploadVideo from './components/pages/uploadVideo/UploadVideo';
import TeacherProfile from './components/pages/profile/TeacherProfile';

import './App.css';

function App() {
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    return (
        <Router>
            <div className="App">
                <Routes>
                    <Route path="/" element={<Role setIsLoggedIn={setIsLoggedIn} />} />
                    <Route path="/auth/login/student" element={<StudentLogin setIsLoggedIn={setIsLoggedIn} />} />
                    <Route path="/auth/login/teacher" element={<TeacherLogin setIsLoggedIn={setIsLoggedIn} />} />
                    <Route path="/auth/register/student" element={<StudentRegister setIsLoggedIn={setIsLoggedIn} />} />
                    <Route path="/auth/register/teacher" element={<TeacherRegister setIsLoggedIn={setIsLoggedIn} />} />

                    {isLoggedIn && (
                        <>
                            <Route path="/student/home" element={<HomePage />} />
                            <Route path="/student/subscriptions" element={<SubscriptionsPage />} />
                            <Route path="/student/history" element={<History />} />
                            <Route path="/student/likedvideos" element={<LikedVideo />} />
                            <Route path="/student/profile" element={<StudentProfile />} />

                            <Route path="/teacher/home" element={<TeacherDashboard />} />
                            <Route path="/teacher/upload" element={<UploadVideo />} />
                            <Route path="/teacher/profile" element={<TeacherProfile />} />

                            <StudentSidebar />
                            <TeacherSidebar />
                        </>
                    )}
                </Routes>
            </div>
        </Router>
    );
}

export default App;
