import React, { useState } from 'react';
import StudentRegisterForm from './StudentRegisterComponent';
import TeacherRegisterForm from './TeacherRegisterComponent';

const RegistrationAndRoleSelection = ({ setIsLoggedIn, setRole }) => {
  const [selectedRole, setSelectedRole] = useState('student');

  const handleRoleChange = (role) => {
    setSelectedRole(role);
  };

  const handleRegistrationSuccess = (role) => {
    setSelectedRole(role);
    setIsLoggedIn(true);
    setRole(role);
  };

  return (
    <div>
      <h2>Register and Select Role</h2>
      <div>
        <label>
          <input
            type="radio"
            value="student"
            checked={selectedRole === 'student'}
            onChange={() => handleRoleChange('student')}
          />
          Student
        </label>
        <label>
          <input
            type="radio"
            value="teacher"
            checked={selectedRole === 'teacher'}
            onChange={() => handleRoleChange('teacher')}
          />
          Teacher
        </label>
      </div>
      {selectedRole === 'student' ? (
        <StudentRegisterForm onSuccess={() => handleRegistrationSuccess('student')} />
      ) : (
        <TeacherRegisterForm onSuccess={() => handleRegistrationSuccess('teacher')} />
      )}
    </div>
  );
};

export default RegistrationAndRoleSelection;
