import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { teacherLogin } from '../../../services/api';
import './Authentication.css';

const TeacherLoginComponent = ({ setIsLoggedIn }) => {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    try {
      await teacherLogin(username, password);
      setIsLoggedIn(true);
      navigate('/teacher/home');
    } catch (error) {
      console.error('Login error:', error);
      alert('Login failed. Please check your username and password.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="login-form">
      <h2>Teacher Login</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit" disabled={isLoading}>
          {isLoading ? 'Logging in...' : 'Login'}
        </button>
      </form>
      <p>
        Don't have an account? <a href="/auth/role-selection">Register here</a>
      </p>
    </div>
  );
};

export default TeacherLoginComponent;
