import React, { useState } from 'react';
import StudentRegisterForm from './StudentRegisterComponent';
import TeacherRegisterForm from './TeacherRegisterComponent';
import StudentLoginForm from './StudentLogin';
import TeacherLoginForm from './TeacherLogin';

const RegistrationAndRoleSelection = ({ setIsLoggedIn }) => {
  const [selectedRole, setSelectedRole] = useState('student');
  const [isRegistering, setIsRegistering] = useState(true);

  const handleRoleChange = (role) => {
    setSelectedRole(role);
  };

  const toggleFormType = () => {
    setIsRegistering(!isRegistering);
  };

  return (
    <div>
      <h2>{isRegistering ? 'Register and Select Role' : 'Login and Select Role'}</h2>
      <div>
        <label>
          <input
            type="radio"
            value="student"
            checked={selectedRole === 'student'}
            onChange={() => handleRoleChange('student')}
          />
          Student
        </label>
        <label>
          <input
            type="radio"
            value="teacher"
            checked={selectedRole === 'teacher'}
            onChange={() => handleRoleChange('teacher')}
          />
          Teacher
        </label>
      </div>
      <button onClick={toggleFormType}>
        {isRegistering ? 'Already registered? Login' : 'New user? Register'}
      </button>
      {isRegistering ? (
        selectedRole === 'student' ? (
          <StudentRegisterForm setIsLoggedIn={setIsLoggedIn} />
        ) : (
          <TeacherRegisterForm setIsLoggedIn={setIsLoggedIn} />
        )
      ) : selectedRole === 'student' ? (
        <StudentLoginForm setIsLoggedIn={setIsLoggedIn} />
      ) : (
        <TeacherLoginForm setIsLoggedIn={setIsLoggedIn} />
      )}
    </div>
  );
};

export default RegistrationAndRoleSelection;
