import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { registerTeacher } from '../../../services/api.js';
import './Authentication.css';

const TeacherRegisterForm = ({ setIsLoggedIn }) => {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setErrorMessage('');

    try {
      await registerTeacher(username, password);
      alert('Teacher registered successfully!');
      setIsLoggedIn(true); 
      navigate('/auth/login/teacher'); 
    } catch (error) {
      setIsLoading(false);
      if (error.message.includes('already exists')) {
        setErrorMessage('User already registered. Please log in.');
        navigate('/auth/login/teacher'); 
      } else {
        setErrorMessage(error.message);
      }
    }
  };

  return (
    <div className="registration-form">
      <h2>Teacher Registration</h2>
      {errorMessage && <p className="error-message">{errorMessage}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit" disabled={isLoading}>
          {isLoading ? 'Registering...' : 'Register'}
        </button>
      </form>
    </div>
  );
};

export default TeacherRegisterForm;
