import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import StudentRegisterForm from './components/pages/authentication/StudentRegisterComponent';
import TeacherRegisterForm from './components/pages/authentication/TeacherRegisterComponent';
import TeacherLoginComponent from './components/pages/authentication/TeacherLogin';
import StudentLoginComponent from './components/pages/authentication/StudentLogin';

import './App.css';

import StudentSidebar from './components/sidebar/StudentSidebar';
import SubscriptionsPage from './components/pages/subscription/Subscription';
import HomePage from './components/pages/home/StudentHome';
import History from './components/pages/history/History';
import LikedVideo from './components/pages/likedVideo/LikedVideo';
import StudentProfile from './components/pages/profile/StudentProfile';

import TeacherSidebar from './components/sidebar/TeacherSidebar';
import TeacherDashboard from './components/pages/home/TeacherHome';
import UploadVideo from './components/pages/uploadVideo/UploadVideo';
import TeacherProfile from './components/pages/profile/TeacherProfile';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  return (
    <Router>
      <div className="App">
        {!isLoggedIn && (
          <header>
          </header>
        )}

        <Routes>
          <Route exact path="/" element={<Navigate to="/auth/register" />} />
          <Route exact path="/auth/login/student" element={<StudentLoginComponent setIsLoggedIn={setIsLoggedIn} />} />
          <Route exact path="/auth/login/teacher" element={<TeacherLoginComponent setIsLoggedIn={setIsLoggedIn} />} />
          <Route exact path="/auth/register/student" element={<StudentRegisterForm setIsLoggedIn={setIsLoggedIn} />} />
          <Route exact path="/auth/register/teacher" element={<TeacherRegisterForm setIsLoggedIn={setIsLoggedIn} />} />

          {isLoggedIn && (
            <>
              <Route path="/student/home" element={<HomePage />} />
              <Route path="/student/subscriptions" element={<SubscriptionsPage />} />
              <Route path="/student/history" element={<History />} />
              <Route path="/student/likedvideos" element={<LikedVideo />} />
              <Route path="/student/profile" element={<StudentProfile />} />

              <Route path="/teacher/home" element={<TeacherDashboard />} />
              <Route path="/teacher/upload" element={<UploadVideo />} />
              <Route path="/teacher/profile" element={<TeacherProfile />} />

              <StudentSidebar />
              <TeacherSidebar />
            </>
          )}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
