import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { registerStudent, registerTeacher } from '../../../services/api';
import './Authentication.css';

const RegistrationAndRoleSelection = ({ setIsLoggedIn, setRole }) => {
  const navigate = useNavigate();
  const [selectedRole, setSelectedRole] = useState('student');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (selectedRole === 'student') {
        await registerStudent(username, password);
      } else {
        await registerTeacher(username, password);
      }
      setIsLoggedIn(true);
      setRole(selectedRole);
      navigate(`/${selectedRole}/home`);
    } catch (error) {
      console.error('Registration error:', error);
      alert('Registration failed. Please try again.');
    }
  };

  return (
    <div className="registration-form">
      <h2>Register and Select Role</h2>
      <form onSubmit={handleSubmit}>
        <div className="role-selection">
          <label>
            <input
              type="radio"
              value="student"
              checked={selectedRole === 'student'}
              onChange={() => setSelectedRole('student')}
            />
            Student
          </label>
          <label>
            <input
              type="radio"
              value="teacher"
              checked={selectedRole === 'teacher'}
              onChange={() => setSelectedRole('teacher')}
            />
            Teacher
          </label>
        </div>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default RegistrationAndRoleSelection;
